      historydat = pushBackStringArray(historydat, "#EXTINF:-1,"+pageTitle);
      historydat = pushBackStringArray(historydat, url);
      writeStringToFile(historyPath, historydat);
      showIdle();
      mode = 0;
	    type="video/mp4"; 
