rss.script:
  translate_base_url  = "http://127.0.0.1/cgi-bin/translate?";

  storagePath             = getStoragePath("tmp");
  storagePath_stream      = storagePath + "stream.dat";
  storagePath_playlist    = storagePath + "playlist.dat";

  error_info          = "";

rss.onEnter:
cachePath = getStoragePath("key");
optionsPath = cachePath + "onehd.dat";
optionsArray = readStringFromFile(optionsPath);
if(optionsArray == null)
{
  buf = "60000";
}
else
{
  buf = getStringArrayAt(optionsArray, 0);
}
startitem = "middle";
info = "2=download, 3=stop download, 1=check dl. status";
setRefreshTime(1);

rss.onExit:
  arr = null;
  arr = pushBackStringArray(arr, buf);
  print("arr=",arr);

  writeStringToFile(optionsPath, arr);

text.script:
idx = getQueryItemIndex();
focus = getFocusItemIndex();
if(focus==idx)
{
  location = getItemInfo(idx, "location");
  annotation = getItemInfo(idx, "annotation");
}
getItemInfo(idx, "title");

fontSize.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "14"; else "14";

backgroundColor.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "10:80:120"; else "-1:-1:-1";

foregroundColor.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "255:255:255"; else "140:140:140";

onUserInput.script:
ret = "false";
userInput = currentUserInput();
titlu="";
if (userInput == "pagedown" || userInput == "pageup")
{
  idx = Integer(getFocusItemIndex());
  if (userInput == "pagedown")
  {
    idx -= -8;
    if(idx >= itemCount)
      idx = itemCount-1;
  }
  else
  {
    idx -= 8;
    if(idx < 0)
      idx = 0;
  }

  print("new idx: "+idx);
  setFocusItemIndex(idx);
	setItemFocus(0);
  "true";
}
else if(userInput == "nine" || userInput == "9")
{
    idx = Integer(getFocusItemIndex());
    idx -= -500;
    if(idx >= itemCount)
    idx = itemCount-1;

  print("new idx: "+idx);
  setFocusItemIndex(idx);
	setItemFocus(0);
  "true";
}
else if(userInput == "seven" || userInput == "7")
{
    idx = Integer(getFocusItemIndex());
    idx -= 500;
    if(idx < 0)
      idx = 0;

  print("new idx: "+idx);
  setFocusItemIndex(idx);
	setItemFocus(0);
  "true";
}
else if(userInput == "six" || userInput == "6")
{
    idx = Integer(getFocusItemIndex());
    idx -= -100;
    if(idx >= itemCount)
    idx = itemCount-1;

  print("new idx: "+idx);
  setFocusItemIndex(idx);
	setItemFocus(0);
  "true";
}
else if(userInput == "four" || userInput == "4")
{
    idx = Integer(getFocusItemIndex());
    idx -= 100;
    if(idx < 0)
      idx = 0;

  print("new idx: "+idx);
  setFocusItemIndex(idx);
	setItemFocus(0);
  "true";
}
else if (userInput == "one" || userInput == "1")
{
  showIdle();
  info=getUrl("http://127.0.0.1/cgi-bin/scripts/util/info_rtmp.php");
  cancelIdle();
  ret="true";
}
else if (userInput == "two" || userInput == "2")
{
  showIdle();
  url1="http://127.0.0.1/cgi-bin/scripts/tv/rock_link.php?file=" + getItemInfo(getFocusItemIndex(),"down");
  dummy=getUrl(url1);
  cancelIdle();
  info = "Start download";
  movie="http://127.0.0.1/cgi-bin/scripts/tv/rock_d.cgi";
  dummy=getUrl(movie);
  ret="true";
}
else if (userInput == "three" || userInput == "3")
{
 topUrl="http://127.0.0.1/cgi-bin/scripts/util/info_rtmp.cgi";
 dummy=getUrl(topUrl);
 info= "Stop download";
 ret = "true";
}
else if (userInput == "zero" || userInput == "0")
{
		if (buf == "60000")
           buf = "40000";
		else if (buf == "40000")
           buf = "20000";
        else if (buf == "20000")
          buf = "10000";
        else if (buf == "10000")
          buf = "8000";
        else if (buf == "8000")
          buf = "6000";
        else if (buf == "6000")
          buf = "4000";
        else if (buf == "4000")
          buf = "2000";
        else if (buf == "2000")
          buf = "1000";
        else if (buf == "1000")
          buf = "500";
        else if (buf == "500")
          buf = "400";
        else if (buf == "400")
          buf = "300";
        else if (buf == "300")
          buf = "200";
        else if (buf == "200")
          buf = "100";
        else if (buf == "100")
          buf = "0";
        else if (buf == "0")
          buf = "60000";
        else
		 buf = "60000";
  ret = "true";
}
redrawDisplay();
ret;

