rss.onRefresh:
setRefreshTime(-1);
itemCount = getPageInfo("itemCount");
middleItem = Integer(itemCount / 2);
redrawDisplay();

image.script:
idx = getQueryItemIndex();
focus = getFocusItemIndex();
if(focus==idx)
{
  location = getItemInfo(idx, "location");
  annotation = getItemInfo(idx, "annotation");
}
getItemInfo(idx, "image");

offsetXPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 0; else 12;

offsetYPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 0; else 6;

widthPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 100; else 75;

heightPC.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) 50; else 37;

fontSize.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "18"; else "14";

foregroundColor.script:
		idx = getQueryItemIndex();
		focus = getFocusItemIndex();
    if(focus==idx) "255:255:255"; else "75:75:75";

onUserInput.script:
      ret = "false";
      userInput = currentUserInput();
      majorContext = getPageInfo("majorContext");

      print("*** majorContext=",majorContext);
      print("*** userInput=",userInput);

      if(userInput == "one" || userInput == "1")
      {
        if(itemCount >= 1)
        {
          setFocusItemIndex(0);
          redrawDisplay();
        }
      }
      else if(userInput == "two" || userInput == "2")
      {
        if(itemCount >= 2)
        {
          setFocusItemIndex(1);
          redrawDisplay();
        }
      }
      else if(userInput == "three" || userInput == "3")
      {
        if(itemCount >= 3)
        {
          setFocusItemIndex(2);
          redrawDisplay();
        }
      }
      else if(userInput == "four" || userInput == "4")
      {
        if(itemCount >= 4)
        {
          setFocusItemIndex(3);
          redrawDisplay();
        }
      }
      else if(userInput == "five" || userInput == "5")
      {
        if(itemCount >= 5)
        {
          setFocusItemIndex(4);
          redrawDisplay();
        }
      }
      else if(userInput == "six" || userInput == "6")
      {
        if(itemCount >= 6)
        {
          setFocusItemIndex(5);
          redrawDisplay();
        }
      }
      else if(userInput == "seven" || userInput == "7")
      {
        if(itemCount >= 7)
        {
          setFocusItemIndex(6);
          redrawDisplay();
        }
      }
      else if(userInput == "eight" || userInput == "8")
      {
        if(itemCount >= 8)
        {
          setFocusItemIndex(7);
          redrawDisplay();
        }
      }
      else if(userInput == "nine" || userInput == "9")
      {
        if(itemCount >= 9)
        {
          setFocusItemIndex(8);
          redrawDisplay();
        }
      }
      if(userInput == "zero" || userInput == "0")
      {
      SetScreenSaverStatus("yes");
      }
      else if (userInput == "pagedown" || userInput == "pageup" || userInput == "PD" || userInput == "PG")
      {
        itemSize = getPageInfo("itemCount");
        idx = Integer(getFocusItemIndex());
        if (userInput == "pagedown")
        {
          idx -= -5;
          if(idx >= itemSize)
            idx = itemSize-1;
        }
        else
        {
          idx -= 5;
          if(idx < 0)
            idx = 0;
        }
        setFocusItemIndex(idx);
        setItemFocus(idx);
        redrawDisplay();
        ret = "true";
      }

      ret;
